createDb.sql_createTable:
create table if not exists User(
user_id Integer primary key,
name TEXT,
password TEXT,
lastLoginTime TEXT
);

create table if not exists Photo(
path TEXT,
time TEXT,
sendTime TEXT
);

create table if not exists Photo(
path TEXT,
time TEXT,
sendTime TEXT,
size TEXT
);

